import React from 'react';
// import PropTypes from 'prop-types';
// import '../assets/css/header.css';

class Form extends React.Component {
  spendigInput() {
    const { value } = this.state;
    return (
      <label htmlFor="value-input">
        Valor:
        <input
          name="value"
          value={ value }
          onChange={ this.handleInputChanges }
          type="number"
          placeholder="R$: Valor gasto."
          data-testid="value-input"
          id="value-input"
          required
        />
      </label>
    );
  }

  currencyInput() {
    const { currNames } = this.state;
    return (
      <label htmlFor="currency-input">
        Moeda:
        <select
          data-testid="currency-input"
          id="currency-input"
          onChange={ this.handleInputChanges }
          name="currency"
        >
          {currNames.map((currencyName) => (
            <option
              key={ currencyName }
              data-testid={ currencyName }
              value={ currencyName }
            >
              {currencyName}
            </option>
          ))}
        </select>
      </label>
    );
  }

  paymentInput() {
    return (
      <label htmlFor="payment-input">
        Metodo de pagamento:
        <select
          data-testid="method-input"
          id="payment-input"
          onChange={ this.handleInputChanges }
          name="method"
        >
          {paymentMethod.map((payment) => (
            <option
              key={ payment }
              data-testid={ payment }
              value={ payment }
            >
              {payment}
            </option>
          ))}
        </select>
      </label>
    );
  }

  tagInput() {
    return (
      <label htmlFor="tag-input">
        Tag:
        <select
          data-testid="tag-input"
          id="tag-input"
          onChange={ this.handleInputChanges }
          name="tag"
        >
          {categories.map((categorie) => (
            <option
              key={ categorie }
              data-testid={ categorie }
              value={ categorie }
            >
              {categorie}
            </option>
          ))}
        </select>
      </label>
    );
  }

  descriptionInput() {
    const { description } = this.state;
    return (
      <label htmlFor="description-input">
        Descrição:
        <input
          type="text"
          placeholder="Mercado, roupa, farm..."
          data-testid="description-input"
          id="value-input"
          name="description"
          value={ description }
          onChange={ this.handleInputChanges }
        />
      </label>
    );
  }

  render() {
    // const { userEmail, totalExpenses } = this.props;
    return (
      <form
        onSubmit={ (event) => {
          event.preventDefault();
          this.handleSubmit(exchangeRates, totalSpendings);
        } }
      >
        { this.spendigInput() }
        { this.currencyInput() }
        { this.paymentInput() }
        { this.tagInput() }
        { this.descriptionInput() }
        <button type="submit">
          Adicionar despesa
        </button>
      </form>
    );
  }
}

// Header.propTypes = {
//   userEmail: PropTypes.string.isRequired,
//   totalExpenses: PropTypes.func.isRequired,
// };

export default Form;
