/* eslint-disable react/prop-types */
import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import '../assets/css/header.css';

class Header extends React.Component {
  constructor() {
    super();
    this.state = {
      totalExpenses: 0,
    };
  }

  componentDidUpdate() {
    this.summAllSpendigns();
  }

  getSpendigsConverted() {
    const { totalExpenses } = this.props;

    if (totalExpenses.length !== 0) {
      const convertedValue = totalExpenses.map((expense) => {
        const { currency, spendigs } = expense;
        const exchangeCurrency = this.findExchangeCurrency(currency);
        const exchanged = this.exchangeToReal(exchangeCurrency, spendigs);
        return exchanged;
      });
      return convertedValue;
    }
  }

  summAllSpendigns() {
    const values = this.getSpendigsConverted();
    if (values) {
      const sumAll = values.reduce((prev, curr) => prev + curr);
      this.setState({ totalExpenses: sumAll });
    }
  }

  exchangeToReal(exchangeCurrency, spendigs) {
    const { ask: exchangeIndex } = exchangeCurrency;
    const conversion = spendigs * exchangeIndex;
    return conversion;
  }

  findExchangeCurrency(currency) {
    const { currencies } = this.props;
    const exchangeCurrency = currencies.find((coin) => coin.code === currency);
    return exchangeCurrency;
  }

  render() {
    const { userEmail } = this.props;
    const { totalExpenses } = this.state;
    const currency = 'BRL';

    return (
      <header className="header-section">
        <div className="page-title">
          <h1> TrybeWallet </h1>
        </div>
        <div className="user-infos">
          <p
            data-testid="email-field"
            className="email-field"
          >
            {`Email: ${userEmail}`}
          </p>
          <div className="spending-field">
            <p data-testid="total-field">{ `Despesas: ${totalExpenses}` }</p>
            <p data-testid="header-currency-field">
              {
                currency
              }
            </p>
          </div>
        </div>
      </header>
    );
  }
}

Header.propTypes = {
  userEmail: PropTypes.string.isRequired,
};

const mapStateToProps = (state) => ({
  userEmail: state.user.email,
  totalExpenses: state.wallet.expenses,
  currencies: state.wallet.allCurr,
});

export default connect(mapStateToProps, null)(Header);
