/* eslint-disable no-shadow */
/* eslint-disable react/prop-types */
import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { loadCurrencies, saveExpense, sumExpensies } from '../actions';
import requestCurrency from '../helpers/requestAPI';
import { categories, paymentMethod } from '../helpers/payment';

class SpendigForm extends React.Component {
  constructor() {
    super();
    this.state = {
      id: 0,
      value: '',
      description: '',
      currency: 'USD',
      method: 'Dinheiro',
      tag: 'Alimentação',
      initialsCurr: [],
      exchangeRates: {},
    };
    this.handleInputChanges = this.handleInputChanges.bind(this);
  }

  async componentDidMount() {
    const allCurrencies = await requestCurrency();
    this.removeUSDT(allCurrencies);
  }

  setCurrencies(currKey, currObj) {
    const { loadCurrencie } = this.props;

    this.setState({
      initialsCurr: currKey,
      exchangeRates: currObj,
    });
    loadCurrencie(currObj);
  }

  getSpendigsConverted() {
    const { totalExpenses } = this.props;

    if (totalExpenses.length !== 0) {
      const convertedValue = totalExpenses.map((expense) => {
        const { currency, value } = expense;
        const exchangeCurrency = this.findExchangeCurrency(currency);
        const exchanged = this.exchangeToReal(exchangeCurrency, value);
        return exchanged;
      });
      return convertedValue;
    }
  }

  summAllSpendigns() {
    const values = this.getSpendigsConverted();
    if (values) {
      const sumAll = values.reduce((prev, curr) => prev + curr);
      return sumAll;
    }
  }

  exchangeToReal(exchangeCurrency, value) {
    const { ask: exchangeIndex } = exchangeCurrency;
    const conversion = value * exchangeIndex;
    return conversion;
  }

  findExchangeCurrency(currency) {
    const { currencies } = this.props;
    const exchangeCurrency = currencies.find((coin) => coin.code === currency);
    return exchangeCurrency;
  }

  removeUSDT(moedas) {
    const currenciesName = Object.keys(moedas)
      .filter((currencyFilter) => currencyFilter !== 'USDT');
    const currenciesObj = Object.values(moedas)
      .filter((currencyFilter) => currencyFilter.codein !== 'BRLT');

    this.setCurrencies(currenciesName, currenciesObj);
  }

  handleInputChanges({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  spendigInput() {
    const { value } = this.state;
    return (
      <label htmlFor="value-input">
        Valor:
        <input
          name="value"
          value={ value }
          onChange={ this.handleInputChanges }
          type="number"
          placeholder="R$: Valor gasto."
          data-testid="value-input"
          id="value-input"
          required
        />
      </label>
    );
  }

  currencyInput() {
    const { initialsCurr } = this.state;
    return (
      <label htmlFor="currency-input">
        Moeda:
        <select
          data-testid="currency-input"
          id="currency-input"
          onChange={ this.handleInputChanges }
          name="currency"
        >
          {initialsCurr.map((currencyName) => (
            <option
              key={ currencyName }
              data-testid={ currencyName }
              value={ currencyName }
            >
              {currencyName}
            </option>
          ))}
        </select>
      </label>
    );
  }

  paymentInput() {
    return (
      <label htmlFor="method-input">
        Metodo de pagamento:
        <select
          data-testid="method-input"
          id="payment-input"
          onChange={ this.handleInputChanges }
          name="method"
        >
          {paymentMethod.map((payment) => (
            <option
              key={ payment }
              data-testid={ payment }
              value={ payment }
            >
              {payment}
            </option>
          ))}
        </select>
      </label>
    );
  }

  tagInput() {
    return (
      <label htmlFor="tag-input">
        Tag:
        <select
          data-testid="tag-input"
          id="tag-input"
          onChange={ this.handleInputChanges }
          name="tag"
        >
          {categories.map((categorie) => (
            <option
              key={ categorie }
              data-testid={ categorie }
              value={ categorie }
            >
              {categorie}
            </option>
          ))}
        </select>
      </label>
    );
  }

  descriptionInput() {
    const { description } = this.state;
    return (
      <label htmlFor="description-input">
        Descrição:
        <input
          type="text"
          placeholder="Mercado, roupa, farm..."
          data-testid="description-input"
          id="value-input"
          name="description"
          value={ description }
          onChange={ this.handleInputChanges }
        />
      </label>
    );
  }

  async handleSubmit() {
    const { id, value, currency, method, tag, description, exchangeRates } = this.state;
    const { addExpenses, sumExpensies } = this.props;
    const currRequest = await requestCurrency();
    const currencies = Object.keys(currRequest)
      .filter((currencyFilter) => currencyFilter !== 'USDT');
    console.log(currencies);
    this.setState({ exchangeRates: currencies });
    const newRegister = {
      id,
      value,
      currency,
      method,
      tag,
      description,
      exchangeRates,
    };
    addExpenses(newRegister);
    this.setState((previousId) => ({ id: previousId.id + 1 }));
    sumExpensies(this.summAllSpendigns());
    const button = document.getElementById('value-input');
    button.value = '';
    // this.summAllSpendigns();
  }

  render() {
    return (
      <section>
        <form
          onSubmit={ (event) => {
            event.preventDefault();
            this.handleSubmit();
          } }
        >
          { this.spendigInput() }
          { this.currencyInput() }
          { this.paymentInput() }
          { this.tagInput() }
          { this.descriptionInput() }
          <button type="submit">
            Adicionar despesa
          </button>
        </form>
      </section>
    );
  }
}

SpendigForm.propTypes = {
  addExpenses: PropTypes.func.isRequired,
  loadCurrencie: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  totalExpenses: state.wallet.expenses,
  currencies: state.wallet.exchangeRates,
});

const mapDispatchToProps = (dispatch) => ({
  addExpenses: (state) => dispatch(saveExpense(state)),
  loadCurrencie: (state) => dispatch(loadCurrencies(state)),
  sumExpensies: (state) => dispatch(sumExpensies(state)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SpendigForm);
