import React from 'react';
import { categories, currencies, payment } from '../helpers/payment';

class SpendigForm extends React.Component {
  constructor() {
    super();
    this.state = {
      spendigs: 0,
      currency: 'BRL',
      payment: '',
      tag: '',
      description: '',
    };
  }

  handleInputChanges({ target }) {
    const { name, value } = target;
    console.log(name, value);
    this.setState({
      [name]: value,
    });
  }

  spendigInput() {
    const { spendigs } = this.state;
    return (
      <label htmlFor="value-input">
        Valor:
        <input
          name="spendigs"
          value={ spendigs }
          onChange={ this.handleInputChanges }
          type="number"
          placeholder="R$: Valor gasto."
          data-testid="value-input"
          id="value-input"
        />
      </label>
    );
  }

  currencyInput() {
    return (
      <label htmlFor="currency-input">
            Moeda:
            <select
              data-testid="currency-input"
              id="currency-input"
            >
              {currencies.map((currency) => (
                <option
                  key={ currency }
                  data-testid={ currency }
                  value={ currency }
                >
                  {currency}
                </option>
              ))}
            </select>
          </label>
    );
  }

  paymentInput() {
    return (
      <label htmlFor="payment-input">
      Metodo de pagamento:
      <select
        data-testid="payment-input"
        id="payment-input"
      >
        {payment.map((method) => (
          <option
            key={ method }
            data-testid={ method }
            value={ method }
          >
            {method}
          </option>
        ))}
      </select>
    </label>
    );
  }

  tagInput() {
    return (
      <label htmlFor="tag-input">
        Metodo de pagamento:
        <select
          data-testid="tag-input"
          id="tag-input"
        >
          {categories.map((categorie) => (
            <option
              key={ categorie }
              data-testid={ categorie }
              value={ categorie }
            >
              {categorie}
            </option>
          ))}
        </select>
      </label>
    );
  }

  descriptionInput() {
    return (
      <label htmlFor="description-input">
        Descrição:
        <input
          type="text"
          placeholder="Mercado, roupa, farm..."
          data-testid="description-input"
          id="value-input"
        />
      </label>
    );
  }

  render() {
    return (
      <section>
        <form>
          { this.spendigInput() }
          { this.currencyInput() }
          { this.paymentInput() }
          { this.tagInput() }
          { this.descriptionInput() }
          <button type="button">
            Adicionar despesa
          </button>
        </form>
      </section>
    );
  }
}

export default SpendigForm;
