/* eslint-disable react/no-unused-state */
import React from 'react';
import { categories, currencies, payment } from '../helpers/payment';

class SpendigForm extends React.Component {
  constructor() {
    super();
    this.state = {
      spendigs: 0,
      currency: 'BRL',
      payment: 'Dinheiro',
      tag: 'Alimentação',
      description: '',
    };
    this.handleInputChanges = this.handleInputChanges.bind(this);
    this.spendigInput = this.spendigInput.bind(this);
    this.currencyInput = this.currencyInput.bind(this);
    this.paymentInput = this.paymentInput.bind(this);
    this.tagInput = this.tagInput.bind(this);
    this.descriptionInput = this.descriptionInput.bind(this);
  }

  handleInputChanges({ target }) {
    const { name, value } = target;
    console.log(name, value);
    this.setState({
      [name]: value,
    });
  }

  spendigInput() {
    const { spendigs } = this.state;
    return (
      <label htmlFor="value-input">
        Valor:
        <input
          name="spendigs"
          value={ spendigs }
          onChange={ this.handleInputChanges }
          type="number"
          placeholder="R$: Valor gasto."
          data-testid="value-input"
          id="value-input"
          required
        />
      </label>
    );
  }

  currencyInput() {
    return (
      <label htmlFor="currency-input">
        Moeda:
        <select
          data-testid="currency-input"
          id="currency-input"
          onChange={ this.handleInputChanges }
          name="currency"
          required
        >
          {currencies.map((currencyName) => (
            <option
              key={ currencyName }
              data-testid={ currencyName }
              value={ currencyName }
            >
              {currencyName}
            </option>
          ))}
        </select>
      </label>
    );
  }

  paymentInput() {
    return (
      <label htmlFor="payment-input">
        Metodo de pagamento:
        <select
          data-testid="payment-input"
          id="payment-input"
          onChange={ this.handleInputChanges }
          name="payment"
        >
          {payment.map((method) => (
            <option
              key={ method }
              data-testid={ method }
              value={ method }
            >
              {method}
            </option>
          ))}
        </select>
      </label>
    );
  }

  tagInput() {
    return (
      <label htmlFor="tag-input">
        Metodo de pagamento:
        <select
          data-testid="tag-input"
          id="tag-input"
          onChange={ this.handleInputChanges }
          name="tag"
        >
          {categories.map((categorie) => (
            <option
              key={ categorie }
              data-testid={ categorie }
              value={ categorie }
            >
              {categorie}
            </option>
          ))}
        </select>
      </label>
    );
  }

  descriptionInput() {
    const { description } = this.state;
    return (
      <label htmlFor="description-input">
        Descrição:
        <input
          type="text"
          placeholder="Mercado, roupa, farm..."
          data-testid="description-input"
          id="value-input"
          name="description"
          value={ description }
          onChange={ this.handleInputChanges }
        />
      </label>
    );
  }

  render() {
    return (
      <section>
        <form
          onSubmit={ (event) => {
            event.preventDefault();
            console.log('dispatch');
          } }
        >
          { this.spendigInput() }
          { this.currencyInput() }
          { this.paymentInput() }
          { this.tagInput() }
          { this.descriptionInput() }
          <button type="submit">
            Adicionar despesa
          </button>
        </form>
      </section>
    );
  }
}

export default SpendigForm;
