import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import '../assets/css/header.css';

class Header extends React.Component {
  constructor() {
    super();
    this.state = {
      timeOut: false,
    };
  }

  componentDidMount() {
    this.timmer();
  }

  timmer() {
    const UM_SEGUNDO = 1000;
    this.setState({ timeOut: true });
    setTimeout(() => {
      this.setState({ timeOut: false });
    }, UM_SEGUNDO);
  }

  formatMonetaryValue(strOrNumber) {
    return `${Number(strOrNumber).toFixed(2)}`;
  }

  totalExpenses() {
    const { timeOut } = this.state;
    const { totalExpenses } = this.props;
    const b = 0;
    const a = this.formatMonetaryValue(totalExpenses);

    return timeOut ? b : a;
  }

  render() {
    const { userEmail } = this.props;
    const Despesas = this.totalExpenses();
    const currency = 'BRL';

    return (
      <header className="header-section">
        <div className="page-title">
          <h1> TrybeWallet </h1>
        </div>
        <div className="user-infos">
          <p
            data-testid="email-field"
            className="email-field"
          >
            {`Email: ${userEmail}`}
          </p>
          <div className="spending-field">
            <p data-testid="total-field">{ `Despesas: ${Despesas}` }</p>
            <p data-testid="header-currency-field">
              {
                currency
              }
            </p>
          </div>
        </div>
      </header>
    );
  }
}

Header.propTypes = {
  userEmail: PropTypes.string.isRequired,
  totalExpenses: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  userEmail: state.user.email,
  totalExpenses: state.wallet.expensesSum,
});

export default connect(mapStateToProps, null)(Header);
