import React from 'react';
// import PropTypes from 'prop-types';
import { connect } from 'react-redux';
// import '../assets/css/header.css';

class SpendigsTable extends React.Component {
  constructor() {
    super();
    this.state = {
    };
  }
  // Fonte: Consultei o repositório do Davi Soares, utilizei de sua lógica e reaproveitei suas funções de conversão do valor;

  formatMonetaryValue(strOrNumber) {
    return `${Number(strOrNumber).toFixed(2)}`;
  }

  convertToBRL(exchangeRates, currency, value) {
    const findExchangeRate = exchangeRates.find((find) => find.code === currency);
    // console.log(findExchangeRate);
    return (findExchangeRate.ask * value);
  }

  teste() {
    const { allExpenses } = this.props;
    allExpenses.map((teste) => console.log('oi'))
  }

  rederExpenseRow(
    { id, description, tag, method, value, currency, exchangeRates },
  ) {
    return (
      <tr key={ id }>
        <td>{ description }</td>
        <td>{ tag }</td>
        <td>{ method }</td>
        <td>{ value }</td>
        <td>{ currency }</td>
        <td>{ exchangeRates }</td>
        <td>
          { this.formatMonetaryValue(
            this.convertToBRL(exchangeRates, currency, value),
          ) }
        </td>
      </tr>
    );
  }

  render() {
    const { allExpenses } = this.props;
    return (
      <table>
        <thead>
          <tr>
            <th>Descrição</th>
            <th>Tag</th>
            <th>Método de pagamento</th>
            <th>Valor</th>
            <th>Moeda</th>
            <th>Câmbio utilizado</th>
            <th>Valor convertido</th>
            <th>Moeda de conversão</th>
            <th>Editar/Excluir</th>
          </tr>
        </thead>
        <tbody>
          {/* {console.log(allExpenses)} */}
          { allExpenses.map((teste) => console.log(teste)) }
        </tbody>
      </table>
    );
  }
}

// SpendigsTable.propTypes = {
//   userEmail: PropTypes.string.isRequired,
//   totalExpenses: PropTypes.func.isRequired,
// };

const mapStateToProps = (state) => ({
  allExpenses: state.wallet.expenses,
  // totalExpenses: state.wallet.expensesSum,
});

export default connect(mapStateToProps, null)(SpendigsTable);
