import React from 'react';
import { connect } from 'react-redux';
import Header from '../components/Header';
import SpendigForm from '../components/SpendingsForm';

class Wallet extends React.Component {
  constructor() {
    super();
    this.state = {
      allExpenses: 0,
    };
  }

  componentDidUpdate() {
    attStateExpense();
  }
  addExpenses() {
  const { totalExpenses } = this.props;
  this.setState({ totalExpenses })
  }

  attStateExpense() {
    const { allExpenses } = this.state;

    if (totalExpenses !== allExpenses) {
      this.setState({ allExpenses: totalExpenses });
    }
    console.log(this.props);
  }

  render() {
    const { allExpenses } = this.state;
    return (
      <>
        <Header allExpenses={ allExpenses } />
        <SpendigForm attStateExpense={ this.attStateExpense } />
      </>
    );
  }
}

const mapStateToProps = (state) => ({
  totalExpenses: state.wallet.expensesSum,
});

export default connect(mapStateToProps, null)(Wallet);
