/* eslint-disable react/prop-types */
import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import '../assets/css/header.css';

class Header extends React.Component {
  constructor() {
    super();
    this.state = {
      totalExpenses: 1500,
    };
  }

  componentDidMount() {
  }

  render() {
    const { userEmail } = this.props;
    const { totalExpenses } = this.state;
    const currency = 'BRL';

    return (
      <header className="header-section">
        <div className="page-title">
          <h1> TrybeWallet </h1>
        </div>
        <div className="user-infos">
          <p
            data-testid="email-field"
            className="email-field"
          >
            {`Email: ${userEmail}`}
          </p>
          <div className="spending-field">
            <p data-testid="total-field">{ `Despesas: ${totalExpenses.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}` }</p>
            <p data-testid="header-currency-field">
              {
                currency
              }
            </p>
          </div>
        </div>
      </header>
    );
  }
}

Header.propTypes = {
  userEmail: PropTypes.string.isRequired,
};

// const mapDispatchToProps = (dispatch) => ({
//   saveUstotalExpenseser: (state) => dispatch(saveEmail(state)),
// });

const mapStateToProps = (state) => ({
  userEmail: state.user.email,
  totalExpenses: state.wallet.expensesSum,
  // currencies: state.wallet.allCurr,
});

export default connect(mapStateToProps, null)(Header);
