/* eslint-disable react/prop-types */
import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import '../assets/css/header.css';

class Header extends React.Component {
  componentDidUpdate() {
    this.teste();
  }

  findExchangeCurrency(currency) {
    const { currencies } = this.props;
    const exchangeCurrency = currencies.find((coin) => coin.code === currency);
    return exchangeCurrency;
  }

  exchangeToReal(exchangeCurrency, spendigs) {
    const { ask: exchangeIndex } = exchangeCurrency;
    const conversion = spendigs * exchangeIndex;
    return conversion;
  }

  async teste() {
    const { totalExpenses, currencies } = this.props;
    // console.log(totalExpenses);
    // const exchangeCurrency = this.findExchangeCurrency()
    if (totalExpenses.length !== 0) {
      totalExpenses.map(async (expense) => {
        const { currency, spendigs } = expense;
        const exchangeCurrency = await this.findExchangeCurrency(currency);
        this.exchangeToReal(exchangeCurrency, spendigs);
        console.log(exchangeToReal);
      });
    }
  }

  render() {
    const { userEmail } = this.props;
    const total = 0;
    const currency = 'BRL';

    return (
      <header className="header-section">
        <div className="page-title">
          <h1> TrybeWallet </h1>
        </div>
        <div className="user-infos">
          <p
            data-testid="email-field"
            className="email-field"
          >
            {`Email: ${userEmail}`}
          </p>
          <div className="spending-field">
            <p data-testid="total-field">{ `Despesas: ${total}` }</p>
            <p data-testid="header-currency-field">
              {
                currency
              }
            </p>
          </div>
        </div>
      </header>
    );
  }
}

Header.propTypes = {
  userEmail: PropTypes.string.isRequired,
};

const mapStateToProps = (state) => ({
  userEmail: state.user.email,
  totalExpenses: state.wallet.expenses,
  currencies: state.wallet.allCurr,
});

export default connect(mapStateToProps, null)(Header);
