/* eslint-disable react/no-unused-state */
/* eslint-disable no-unused-vars */
/* eslint-disable react/prop-types */
import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import '../assets/css/header.css';

class Header extends React.Component {
  constructor() {
    super();
    this.state = {
      totalExpenses: 0,
      timeOut: false,
    };
  }

  componentDidMount() {
    this.timmer();
  }

  async timmer() {
    const { timeOut } = this.state;
    const UM_SEGUNDO = 1000;
    this.setState({ timeOut: true });
    setTimeout(() => {
      this.setState({ timeOut: false });
    }, UM_SEGUNDO);
  }

  totalExpenses() {
    const { timeOut } = this.state;
    const { totalExpenses } = this.props;
    console.log(totalExpenses);
    const b = 'Despesas: 0';
    const a = `Despesas: ${totalExpenses
      .toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}`;
    return timeOut ? b : a;
  }

  render() {
    const { userEmail, totalExpenses } = this.props;
    // const { totalExpenses } = this.state;
    const currency = 'BRL';

    return (
      <header className="header-section">
        <div className="page-title">
          <h1> TrybeWallet </h1>
        </div>
        <div className="user-infos">
          <p
            data-testid="email-field"
            className="email-field"
          >
            {`Email: ${userEmail}`}
          </p>
          <div className="spending-field">
            <p data-testid="total-field">{ this.totalExpenses() }</p>
            <p data-testid="header-currency-field">
              {
                currency
              }
            </p>
          </div>
        </div>
      </header>
    );
  }
}

Header.propTypes = {
  userEmail: PropTypes.string.isRequired,
};

// const mapDispatchToProps = (dispatch) => ({
//   saveUstotalExpenseser: (state) => dispatch(saveEmail(state)),
// });

const mapStateToProps = (state) => ({
  userEmail: state.user.email,
  totalExpenses: state.wallet.expensesSum,
  // currencies: state.wallet.allCurr,
});

export default connect(mapStateToProps, null)(Header);
