  // }

  setExpenses() {
    const { totalExpenses: expenses } = this.props;
    const { totalExpenses } = this.state;
    console.log(expenses, totalExpenses);

    if (expenses === 0) {
      return totalExpenses;
    }

    return expenses;
  }

  render() {
    const { userEmail, allExpenses } = this.props;

    const currency = 'BRL';

    return (
      <header className="header-section">
        <div className="page-title">
          <h1> TrybeWallet </h1>
        </div>
        <div className="user-infos">
          <p
            data-testid="email-field"
            className="email-field"
          >
            {`Email: ${userEmail}`}
          </p>
          <div className="spending-field">
            <p data-testid="total-field">{ `Despesas: ${allExpenses.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}` }</p>
            <p data-testid="header-currency-field">
              {
                currency
              }
            </p>
          </div>
        </div>
      </header>
    );
  }
}

Header.propTypes = {
  userEmail: PropTypes.string.isRequired,
};

// const mapDispatchToProps = (dispatch) => ({
