import React from 'react';
import { connect } from 'react-redux';
import { saveEmail } from '../actions';
import '../assets/css/loginPage.css';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      userEmail: '',
      userPassword: '',
    };
    this.verifyEmail = this.verifyEmail.bind(this);
    this.verifyPassword = this.verifyPassword.bind(this);
    this.handleInputChanges = this.handleInputChanges.bind(this);
    this.handleSubmitButton = this.handleSubmitButton.bind(this);
  }

  componentDidMount() {
    this.verifyButton();
  }

  componentDidUpdate() {
    this.verifyButton();
  }

  verifyButton() {
    const button = document.querySelector('button');
    button.disabled = true;
    const checkEmail = this.verifyEmail();
    const checkPass = this.verifyPassword();
    if (checkEmail && checkPass) {
      button.disabled = false;
    }
  }

  verifyEmail() {
    const { userEmail } = this.state;
    // validação REGEX simplificada de email, fonte: https://stackoverflow.com/questions/46155/how-to-validate-an-email-address-in-javascript
    const validationRegex = /\S+@\S+\.\S+/;
    const validation = validationRegex.test(userEmail);
    return validation;
  }

  verifyPassword() {
    const { userPassword } = this.state;
    const minimumLength = 6;
    const passLangth = userPassword.length;
    if (passLangth >= minimumLength) {
      return true;
    }
    return false;
  }

  handleInputChanges({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  // handleSubmitButton(event) {
  //   event.preventDefault();
  // }

  render() {
    const { userPassword, userEmail } = this.state;
    const { saveUser } = this.props;

    return (
      <div id="main-div">
        <form id="form-sec">
          <label htmlFor="userEmail">
            <input
              data-testid="email-input"
              name="userEmail"
              type="email"
              placeholder="Digite seu Email"
              value={ userEmail }
              onChange={ this.handleInputChanges }
              required
            />
          </label>
          <label htmlFor="userPassword">
            <input
              data-testid="password-input"
              type="password"
              name="userPassword"
              placeholder="Digite sua senha"
              value={ userPassword }
              onChange={ this.handleInputChanges }
              required
            />
          </label>
          <button
            id="submit-button"
            type="submit"
            onClick={ (event) => {
              event.preventDefault();
              saveUser(userEmail);
            } }
          >
            Entrar
          </button>
        </form>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  saveUser: (state) => dispatch(saveEmail(state)),
});

export default connect(null, mapDispatchToProps)(Login);
