import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { saveExpense } from '../actions';
import requestCurrency from '../helpers/requestAPI';
import { categories, payment } from '../helpers/payment';

class SpendigForm extends React.Component {
  constructor() {
    super();
    this.state = {
      id: 0,
      spendigs: 0,
      currency: 'BRL',
      payment: 'Dinheiro',
      tag: 'Alimentação',
      description: '',
      initialsCurr: [],
      allCurr: {},
    };
    this.handleInputChanges = this.handleInputChanges.bind(this);
  }

  async componentDidMount() {
    const allCurrencies = await requestCurrency();
    this.setCurrencies(allCurrencies);
  }

  setCurrencies(moedas) {
    const allCurrencies = [];
    Object.keys(moedas).map((moeda) => (
      allCurrencies.push(moeda)
    ));
    this.setState({ initialsCurr: allCurrencies });
  }

  handleInputChanges({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  spendigInput() {
    const { spendigs } = this.state;
    return (
      <label htmlFor="value-input">
        Valor:
        <input
          name="spendigs"
          value={ spendigs }
          onChange={ this.handleInputChanges }
          type="number"
          placeholder="R$: Valor gasto."
          data-testid="value-input"
          id="value-input"
          required
        />
      </label>
    );
  }

  currencyInput() {
    const { initialsCurr } = this.state;
    return (
      <label htmlFor="currency-input">
        Moeda:
        <select
          data-testid="currency-input"
          id="currency-input"
          onChange={ this.handleInputChanges }
          name="currency"
        >
          {initialsCurr.map((currencyName) => (
            <option
              key={ currencyName }
              data-testid={ currencyName }
              value={ currencyName }
            >
              {currencyName}
            </option>
          ))}
        </select>
      </label>
    );
  }

  paymentInput() {
    return (
      <label htmlFor="payment-input">
        Metodo de pagamento:
        <select
          data-testid="payment-input"
          id="payment-input"
          onChange={ this.handleInputChanges }
          name="payment"
        >
          {payment.map((method) => (
            <option
              key={ method }
              data-testid={ method }
              value={ method }
            >
              {method}
            </option>
          ))}
        </select>
      </label>
    );
  }

  tagInput() {
    return (
      <label htmlFor="tag-input">
        Tag:
        <select
          data-testid="tag-input"
          id="tag-input"
          onChange={ this.handleInputChanges }
          name="tag"
        >
          {categories.map((categorie) => (
            <option
              key={ categorie }
              data-testid={ categorie }
              value={ categorie }
            >
              {categorie}
            </option>
          ))}
        </select>
      </label>
    );
  }

  descriptionInput() {
    const { description } = this.state;
    return (
      <label htmlFor="description-input">
        Descrição:
        <input
          type="text"
          placeholder="Mercado, roupa, farm..."
          data-testid="description-input"
          id="value-input"
          name="description"
          value={ description }
          onChange={ this.handleInputChanges }
        />
      </label>
    );
  }

  async handleSubmit() {
    const { id, spendigs, currency, payment, tag, description, allCurr } = this.state;
    const { addExpenses } = this.props;
    const currRequest = requestCurrency();
    addExpenses(this.state);
    this.setState((previousId) => ({ id: previousId.id + 1 }));
  }

  render() {
    return (
      <section>
        <form
          onSubmit={ (event) => {
            event.preventDefault();
            this.handleSubmit();
          } }
        >
          { this.spendigInput() }
          { this.currencyInput() }
          { this.paymentInput() }
          { this.tagInput() }
          { this.descriptionInput() }
          <button type="submit">
            Adicionar despesa
          </button>
        </form>
      </section>
    );
  }
}

SpendigForm.propTypes = {
  addExpenses: PropTypes.func.isRequired,
};

const mapDispatchToProps = (dispatch) => ({
  addExpenses: (state) => dispatch(saveExpense(state)),
});

export default connect(null, mapDispatchToProps)(SpendigForm);
