import { SAVE_NEW_EXPENSE, NEW_EXPENSE } from '../actions';

// Esse reducer será responsável por tratar o todas as informações relacionadas as despesas
const INITIAL_STATE = {
  expenses: [],
};

const walletReducer = (state = INITIAL_STATE, action) => {
  // const currentExpenseIndex = state.expenses.length - 1;
  // const currentExpense = state.expenses[currentExpenseIndex];
  // const allExpesesWithoutLast = state.expenses.slice(0, currentExpenseIndex);
  switch (action.type) {
  case SAVE_NEW_EXPENSE:
    return {
      ...state,
      expenses: [...state, action.payload],
    };
  case NEW_EXPENSE:
    return {
      ...state,
      expenses: [
        ...state.expenses,
        { ...action.payload },
      ],
    };
  default:
    return state;
  }
};

export default walletReducer;
