import React from 'react';
import '../assets/css/loginPage.css';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      userEmail: 'viegas@hotmail.com',
      userPassword: 'aasdasdsd',
      validadeEmail: false,
      validadePass: false,
    };
    this.verifyEmail = this.verifyEmail.bind(this);
    this.verifyPassword = this.verifyPassword.bind(this);
    this.handleInputChanges = this.handleInputChanges.bind(this);
    this.handleSubmitButton = this.handleSubmitButton.bind(this);
  }

  componentDidUpdate() {

  }

  verifyEmail() {
    const { userEmail } = this.state;
    // validação REGEX simplificada de email, fonte: https://stackoverflow.com/questions/46155/how-to-validate-an-email-address-in-javascript
    const re = /\S+@\S+\.\S+/;
    return re.test(userEmail);
  }

  verifyPassword() {
    const { userPassword, validadePass } = this.state;
    const minimumLength = 6;
    if (userPassword >= minimumLength) {
      this.setState({ validadePass: true });
    }
    this.setState({ validadePass: false });

  }

  handleInputChanges() {

  }

  handleSubmitButton(event) {
    event.preventDefault();
    const checkEmail = this.verifyEmail();
    const checkPass = this.verifyPassword();
    console.log(checkEmail, checkPass);
  }

  render() {
    return (
      <div id="main-div">
        <section id="form-sec">
          <label htmlFor="email-login">
            <input
              data-testid="email-input"
              type="email"
              name="email-login"
              placeholder="Digite seu Email"
            />
          </label>
          <label htmlFor="pass-login">
            <input
              data-testid="password-input"
              type="text"
              name="pass-login"
              placeholder="Digite sua senha"
            />
          </label>
          <button
            id="submit-button"
            type="submit"
            onClick={ this.verifyPassword }
          >
            Entrar
          </button>
        </section>
      </div>
    );
  }
}

export default Login;
